drop database if exists eiketsu;
create database eiketsu default character set utf8 collate utf8_general_ci;
drop user if exists 'user'@'localhost';
create user 'user'@'localhost' identified by 'password';
grant all on eiketsu.* to 'user'@'localhost';
use eiketsu;

create table general (
    id int auto_increment primary key,
    name varchar(200) not null,
    name_furigana varchar(200) not null,
    color varchar(200) not null,
    number varchar(200) not null,
    appear varchar(200) not null,
    rarity varchar(200) not null,
    period varchar(200) not null,
    type_of_solder varchar(200) not null,
    cost decimal(2, 1) not null,
    power int not null,
    intelligence int not null,
    special_skill_none varchar(200) not null,
    special_skill_ambush varchar(200) not null,
    special_skill_guardrail varchar(200) not null,
    special_skill_revival varchar(200) not null,
    special_skill_tolerance varchar(200) not null,
    special_skill_fighting_spirit varchar(200) not null,
    special_skill_sniping varchar(200) not null,
    special_skill_enhancement varchar(200) not null,
    special_skill_technique varchar(200) not null,
    special_skill_vanguard varchar(200) not null,
    special_skill_ogre varchar(200) not null,
    stratagem_name varchar(200) not null,
    stratagem_name_furigana varchar(200) not null,
    stratagem_category varchar(200) not null,
    necessary_morale int not null,
    effect_time varchar(200) not null,
    stratagem_kinds varchar(200) not null,
    stratagem_explanation varchar(200) not null,
    illustration varchar(200) not null,
    cv varchar(200) not null
);

create table user (
    id int auto_increment primary key,
    name varchar(100) not null,
    login varchar(100) not null unique,
    password varchar(100) not null
);

create table favorite (
    user_id int,
    general_id int,
    foreign key(user_id) references user(id),
    foreign key(general_id) references general(id)
);






